import http.client
import urllib.parse
import logging

def http_request(method, action, param=None, **params):
    if param is None:
        param = {}
    param.update(params)
    parsed_url = urllib.parse.urlparse(action)
    host = parsed_url.netloc
    path = parsed_url.path or '/'
    query_params = urllib.parse.urlencode(param)
    headers = {'Content-type': 'application/x-www-form-urlencoded'}

    if parsed_url.scheme == 'https':
        connection_class = http.client.HTTPSConnection
    else:
        connection_class = http.client.HTTPConnection

    try:
        conn = connection_class(host)
        logging.debug(f"Connecting to {host} using {parsed_url.scheme}")

        if method == 'GET':
            full_path = f"{path}?{query_params}" if query_params else path
            conn.request('GET', full_path, headers=headers)
        elif method == 'POST':
            conn.request('POST', path, body=query_params, headers=headers)
        else:
            raise ValueError("Unsupported HTTP method: {}".format(method))

        response = conn.getresponse()
        logging.debug(f"Response status: {response.status}, reason: {response.reason}")

        data = response.read().decode('utf-8')
        if 200 <= response.status < 300:
            return data
        else:
            raise Exception(f"HTTP request failed with status code {response.status}: {data}")
    except Exception as e:
        logging.exception("An error occurred during the HTTP request")
        raise
    finally:
        if 'conn' in locals():
            conn.close()
            logging.debug("Connection closed")
