from flask import Flask, request, Response
import base64
import json
import logging
from google.cloud import bigquery

app = Flask(__name__)
bq_client = bigquery.Client()

TABLE_ID = "your-project.your_dataset.your_table"

def process_message_data(data):
    try:
        decoded_data = base64.b64decode(data).decode("utf-8")
        return json.loads(decoded_data)
    except Exception as e:
        logging.error(f"Error decoding message data: {e}")
        raise

def handle_cloud_build_event(attributes, message_data):
    build_id = attributes.get("buildId")
    if not build_id:
        return None

    try:
        cloud_build_data = {
            "build_id": build_id,
            "attributes": attributes,
            "message_data": message_data
        }
        return cloud_build_data
    except Exception as e:
        logging.error(f"Error processing Cloud Build event: {e}")
        raise

@app.route("/", methods=["POST"])
def pubsub_push_handler():
    try:
        envelope = request.get_json()
        if not envelope or "message" not in envelope:
            raise ValueError("Invalid Pub/Sub message format")

        message = envelope["message"]
        attributes = message.get("attributes", {})
        data = message.get("data")

        if data:
            message_data = process_message_data(data)
        else:
            message_data = {}

        cloud_build_event = handle_cloud_build_event(attributes, message_data)

        if cloud_build_event:
            dataset_ref = bq_client.dataset(TABLE_ID.split('.')[1])
            table_ref = dataset_ref.table(TABLE_ID.split('.')[2])
            table = bq_client.get_table(table_ref)

            rows_to_insert = [cloud_build_event]
            errors = bq_client.insert_rows_json(table, rows_to_insert)

            if errors:
                for error in errors:
                    logging.error(f"BigQuery insert error: {error}")

        return Response(status=204)
    except Exception as e:
        logging.exception(f"Error processing Pub/Sub message: {e}")
        raise
