        script = f"""
        app.preferences.rulerUnits = Units.PIXELS;
        var doc = app.open(FilePath);
        var layer = doc.artLayers.add();
        layer.name = "Pasted Image";
        var idPlcE = charIDToTypeID("PlcE");
        var desc2 = new ActionDescriptor();
        var idnull = charIDToTypeID("null");
        var ref2 = new ActionReference(idPlcE, [doc.id]);
        var idT   = charIDToTypeID("T   ");
        var idChnl = charIDToTypeID("Chnl");
        var idOrdn = charIDToTypeID("Ordn");
        var idTrgt = charIDToTypeID("Trgt");
        ref2.putClass(idT, idChnl);
        ref2.putEnumerated(idOrdn, idOrdn, idTrgt);
        desc2.putReference(idnull, ref2);
        executeAction(idPlcE, desc2, DialogModes.NO);
        """
        script = script.replace("FilePath", f'"{image_path}"')
        with open('temp.jsx', 'w') as f:
            f.write(script)
        subprocess.run([
            'osascript',
            '-e', 'tell application "Adobe Photoshop"',
            '-e', f'do JavaScript file "{os.path.abspath("temp.jsx")}" with options {{"password": "{password}"}}',
            '-e', 'end tell'
        ])
