from sanic import Sanic, response
from sanic.request import Request
import aiofiles
import asyncio
from io import BytesIO

class SanicHttpClient:
    def __init__(
        self,
        graphiql: Optional[bool] = None,
        graphql_ide: Optional[str] = "graphiql",
        allow_queries_via_get: bool = False,
        result_override: Optional[Any] = None,
        multipart_uploads_enabled: bool = True,
    ) -> None:
        self.app = Sanic("SanicHttpClient")
        self.graphiql = graphiql
        self.graphql_ide = graphql_ide
        self.allow_queries_via_get = allow_queries_via_get
        self.result_override = result_override
        self.multipart_uploads_enabled = multipart_uploads_enabled

        @self.app.route("/graphql", methods=["GET", "POST"])
        async def graphql_route(request: Request):
            return await self._graphql_request(
                method=request.method.lower(),
                query=request.args.get("query") or request.json.get("query"),
                variables=request.json.get("variables"),
                files=cast(Dict[str, BytesIO], request.files),
                headers=request.headers,
            )

    async def _graphql_request(
        self,
        method: Literal["get", "post"],
        query: Optional[str],
        variables: Optional[Dict[str, object]],
        files: Optional[Dict[str, BytesIO]],
        headers: Optional[Dict[str, str]],
        **kwargs: Any,
    ) -> response.BaseHTTPResponse:
        if not query:
            return response.json({"error": "GraphQL query is required"}, status=400)

        if method == "get" and not self.allow_queries_via_get:
            return response.json({"error": "GET requests are not allowed for GraphQL queries"}, status=405)

        payload = {"query": query}
        if variables:
            payload["variables"] = variables

        if files and self.multipart_uploads_enabled:
            form = aiohttp.FormData()
            form.add_field("operations", json.dumps(payload))
            file_map = {}
            for name, file in files.items():
                file_key = f"{name}"
                file_map[file_key] = [name]
                form.add_field(name, file.getvalue(), filename=name)
            payload["map"] = file_map
            headers = {**headers, "Content-Type": "multipart/form-data"}
            return response.raw(b"", headers=headers)

        return response.json(self.result_override or {"data": {"result": "success"}})

    async def request(
        self,
        url: str,
        method: Literal["get", "post", "patch", "put", "delete"],
        headers: Optional[Dict[str, str]] = None,
    ) -> response.BaseHTTPResponse:
        if method.upper() not in ["GET", "POST", "PATCH", "PUT", "DELETE"]:
            return response.json({"error": "Unsupported HTTP method"}, status=405)

        return response.text(f"{method.upper()} request to {url}", headers=headers or {})

    async def get(
        self,
        url: str,
        headers: Optional[Dict[str, str]] = None,
    ) -> response.BaseHTTPResponse:
        return await self.request(url, "get", headers=headers)

    async def post(
        self,
        url: str,
        data: Optional[bytes] = None,
        json: Optional[Dict] = None,
        headers: Optional[Dict[str, str]] = None,
    ) -> response.BaseHTTPResponse:
        content_type = headers.get("Content-Type") if headers else None
        body = data or (json.dumps(json).encode() if json else b"")
        return response.raw(body, headers={**(headers or {}), "Content-Type": content_type or "application/json"})
