            if dyn_key in self.dynamic_files:
                content = self.dynamic_files[dyn_key]
                headers = {'Content-Type': 'application/octet-stream', 'Content-Length': str(len(content))}
                self._respond(200, headers, content)
            else:
                self._respond(404, {'Content-Type': 'text/plain'}, b'Dynamic file not found')
        elif path.startswith('/range/'):
            size = int(path[6:])
            start = 0
            end = size - 1
            if 'Range' in self.headers:
                range_header = self.headers['Range']
                if range_header.startswith('bytes='):
                    range_values = range_header[6:].split('-')
                    start = int(range_values[0])
                    if len(range_values) > 1:
