import tornado.ioloop
import tornado.web
import os

class MainHandler(tornado.web.RequestHandler):
    def get(self, project):
        coverage_file = f"/tmp/{project}-coverage"
        if not os.path.exists(coverage_file):
            self.set_status(404)
            self.write("Project coverage file not found")
            return

        with open(coverage_file, 'r') as f:
            try:
                coverage = float(f.read().strip())
            except ValueError:
                self.set_status(500)
                self.write("Invalid coverage data")
                return

        if coverage >= 80:
            color = "green"
        else:
            color = "yellow"

        badge_url = f"https://img.shields.io/badge/coverage-{int(coverage)}%25-{color}.svg"
        self.redirect(badge_url)

class CoverageBadgeServer:
    def __init__(self, port: int = 8888, address: str = '127.0.0.1'):
        self.port = port
        self.address = address
        self.application = tornado.web.Application([
            (r"/badge/(.*)", MainHandler),
        ])

    def start(self):
        self.application.listen(self.port, address=self.address)
