from typing import Optional
import concurrent.futures


class DefaultProducer(KafkaProducer):
    def send(self, topic: str, *, key: Optional[bytes] = None, value: Optional[bytes] = None) -> concurrent.futures.Future:
        future = super().send(topic, key=key, value=value)

        def on_success(metadata):
            future.set_result(metadata)

        def on_error(exception):
            future.set_exception(exception)

        future.add_callback(on_success)
        future.add_errback(on_error)
        return future

    def __enter__(self) -> 'DefaultProducer':
        return self

    def __exit__(self, *args) -> None:
        self.close()
