import sys


def execute(cmd, shell=False, poll_period=0.1, catch_out=False, executable=None, env=None):
    if isinstance(cmd, str):
        if not shell:
            cmd = shlex.split(cmd)
    else:
        cmd = list(cmd)

    stdout_pipe = subprocess.PIPE if catch_out else None
    stderr_pipe = subprocess.PIPE if catch_out else None

