        cmd,
        stdin=subprocess.DEVNULL,
        stdout=stdout_pipe,
        stderr=stderr_pipe,
        shell=shell,
        executable=executable,
        env=env
    )

    stdout_data, stderr_data = process.communicate()

    if sys.version_info[0] >= 3 and isinstance(stdout_data, bytes) and catch_out:
        stdout_data = stdout_data.decode(errors='replace')
    if sys.version_info[0] >= 3 and isinstance(stderr_data, bytes) and catch_out:
        stderr_data = stderr_data.decode(errors='replace')

    return (process.returncode, stdout_data, stderr_data)


