        if not is_file:
            with open("temp_input.txt", "w") as f:
                f.write(input)
            command[2] = "temp_input.txt"

        try:
            process = subprocess.Popen(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
            stdout, stderr = process.communicate()

            if process.returncode != 0:
                self._logger.error(f"Phonetisaurus execution failed: {stderr}")
                raise RuntimeError(f"Phonetisaurus G2P execution failed: {stderr}")

            lines = stdout.strip().splitlines()
            for line in lines:
                parts = line.split(" ", 1)
                if len(parts) < 2:
                    continue
                word, pronunciation = parts
                if word not in result_dict:
                    result_dict[word] = []
                result_dict[word].append(pronunciation)

            if not is_file:
                import os
                os.remove("temp_input.txt")

        except Exception as e:
            self._logger.exception("Error during Phonetisaurus execution.")
            raise

        return result_dict
