import subprocess
from collections import defaultdict


class OssecCollector:
    def __init__(self, bin_path='/var/ossec/bin/agent_control', use_sudo=True, sudo_cmd='/usr/bin/sudo'):
        self.bin_path = bin_path
        self.use_sudo = use_sudo
        self.sudo_cmd = sudo_cmd

    def collect(self):
        state_counts = defaultdict(int)
        command = [self.sudo_cmd, self.bin_path, '-l'] if self.use_sudo else [self.bin_path, '-l']

        try:
            process = subprocess.Popen(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
            stdout, stderr = process.communicate()

            if process.returncode != 0:
                raise RuntimeError(f"Command failed with error: {stderr}")

            for line in stdout.splitlines():
                if line.startswith('Agent') and '(' in line:
                    status = line.split('(')[1].split(')')[0]
                    state_counts[status] += 1

        except Exception as e:
            raise RuntimeError(f"Failed to execute or parse OSSEC agent control output: {e}")

        return dict(state_counts)
