class Partclone:
    @staticmethod
    def get_partclone_info_dict(abs_partclone_image_list, image_key, compression):
        info_output = ""
        try:
            if not abs_partclone_image_list:
                return {image_key: {"filesystem": "<unknown>"}}

            # Build command chain: cat -> decompress -> partclone.info
            commands = []

            for image in abs_partclone_image_list:
                cat_cmd = f"cat {shlex.quote(image)}"
                decompress_cmd = ""

                if compression == "gzip":
                    decompress_cmd = "gzip -d -c"
                elif compression == "bzip2":
                    decompress_cmd = "bzip2 -d -c"
                elif compression == "xz":
                    decompress_cmd = "xz -d -c"
                else:
                    decompress_cmd = "cat"

                partclone_info_cmd = "partclone.info --output-raw"

                cmd_chain = f"{cat_cmd} | {decompress_cmd} | {partclone_info_cmd}"
                process = subprocess.Popen(cmd_chain, shell=True, stdout=subprocess.PIPE, stderr=subprocess.DEVNULL)
                stdout, _ = process.communicate()
                info_output += stdout.decode("utf-8", errors="ignore")

            parsed_info = Partclone.parse_partclone_info_output(info_output)
            filesystem = parsed_info.get("Filesystem", "<unknown>")
            return {image_key: {"filesystem": filesystem}}
        except Exception as e:
            return {image_key: {"filesystem": "<unknown>"}}

    @staticmethod
    def parse_partclone_info_output(output):
        result = {}
        lines = output.strip().splitlines()
        for line in lines:
            match = re.match(r"^(\S+):\s*(.+)$", line)
            if match:
                key, value = match.groups()
                result[key.strip()] = value.strip()
        return result
