import re

def html_to_text(data, baseurl=None, method='lynx', options=None):
    if options is None:
        options = {}

    if method == 'lynx':
        args = ['lynx', '-stdin', '-dump']
        if 'width' in options:
            args.extend(['-width', str(options['width'])])
        if 'nolist' in options:
            args.append('-nolist')
    elif method == 'html2text':
        args = ['html2text']
        for key, value in options.items():
            if value is None:
                args.append('--%s' % key.replace('_', '-'))
            else:
                args.append('--%s' % key.replace('_', '-'))
                args.append(str(value))
    else:
        raise ValueError('Unsupported method: %s' % method)

    proc = subprocess.Popen(args, stdin=subprocess.PIPE, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
    stdout_data, stderr_data = proc.communicate(input=data.encode('utf-8'))

    if proc.returncode != 0:
        raise RuntimeError('Conversion failed with error: %s' % stderr_data.decode('utf-8'))

    result = stdout_data.decode('utf-8')

    if method == 'lynx':
        result = re.sub(r'^$[\\r\\n]+', '', result, flags=re.MULTILINE)

