import httplib
import urllib.parse
import json
import time

DEFAULT_HTTP_TIMEOUT = 30


class BaseProxy:
    def __init__(self, service_url=None, service_port=None, btc_conf_file=None, timeout=DEFAULT_HTTP_TIMEOUT, connection=None):
        if connection:
            self.__conn = connection
        else:
            if not service_url:
                raise ValueError("service_url is required")
            parsed_url = urllib.parse.urlparse(service_url)
            if parsed_url.scheme not in ('http', 'https'):
                raise ValueError("Unsupported URL scheme: %s" % parsed_url.scheme)
            self.__url = parsed_url
            self.__service_url = service_url
            cls = httplib.HTTPSConnection if parsed_url.scheme == 'https' else httplib.HTTPConnection
            self.__conn = cls(parsed_url.hostname, parsed_url.port or service_port, timeout=timeout)
        self.__id_count = 0
        self.__auth_header = None

    def _call(self, service_name, *args):
        self.__id_count += 1
        request = {
            "jsonrpc": "2.0",
            "method": service_name,
            "params": args,
            "id": self.__id_count
        }
        headers = {"Content-Type": "application/json"}
        if self.__auth_header:
            headers["Authorization"] = self.__auth_header
        payload = json.dumps(request)
        self.__conn.request("POST", self.__url.path or "/", payload, headers)
        return self._get_response()

    def _get_response(self):
        response = self.__conn.getresponse()
        data = response.read()
        if response.status < 200 or response.status >= 300:
            raise Exception(f"HTTP error {response.status}: {data}")
        try:
            result = json.loads(data)
        except Exception as e:
            raise Exception(f"Failed to parse JSON response: {e}")
        if "error" in result:
            raise Exception(f"RPC error: {result['error']}")
        return result["result"]

    def close(self):
        if self.__conn:
            self.__conn.close()
            self.__conn = None

    def __del__(self):
        self.close()
