import socket
import struct
import select
import time

class socksocket(socket.socket):
    default_proxy = None

    def __init__(self, family=socket.AF_INET, type=socket.SOCK_STREAM, proto=0, *args, **kwargs):
        if type not in (socket.SOCK_STREAM, socket.SOCK_DGRAM):
            raise socket.error("Only SOCK_STREAM and SOCK_DGRAM are supported")
        self._proxyconn = None
        self.proxy = None
        self.proxy_sockname = None
        self.proxy_peername = None
        super().__init__(family, type, proto, *args, **kwargs)

    def set_proxy(self, proxy_type, addr, port, rdns=True, username=None, password=None):
        self.proxy = (proxy_type, addr, port, rdns, username, password)
        if self.default_proxy is None:
            self.default_proxy = self.proxy

    def connect(self, dest_pair):
        if not self.proxy:
            return super().connect(dest_pair)
        else:
            self._negotiate(*dest_pair)

    def _readall(self, file, count):
        data = b''
        while len(data) < count:
            chunk = file.recv(count - len(data))
            if not chunk:
                raise socket.error("Connection closed while receiving data")
            data += chunk
        return data

    def _proxy_addr(self):
        proxy_type, addr, port, _, _, _ = self.proxy
        if isinstance(addr, str):
            try:
                host, serv = addr.rsplit(':', 1)
                port = int(serv)
                addr = host
            except ValueError:
                pass
        return (addr, port)

    def _negotiate_SOCKS4(self, dest_addr, dest_port):
        import socks
        ip = socket.inet_aton(dest_addr)
        user_id = b'\x00'
        msg = b'\x04\x01' + struct.pack('>H', dest_port) + ip + user_id
        self.sendall(msg)
        resp = self._readall(self, 8)
        if ord(resp[1:2]) != 90:
            raise socket.error(f"SOCKS4 negotiation failed: {resp}")

    def _negotiate_SOCKS5(self, dest_addr, dest_port):
        methods = [0]  # No authentication
        self.sendall(b"\x05\x01" + bytes(methods))
        resp = self._readall(self, 2)
        if ord(resp[1:2]) != 0:
            raise socket.error("No acceptable authentication method found")

        atyp = socket.AF_INET if ':' not in dest_addr else socket.AF_INET6
        if atyp == socket.AF_INET:
            addr = socket.inet_aton(dest_addr)
        else:
            raise socket.error("IPv6 support for SOCKS5 not implemented here")
        cmd = b"\x01"  # CONNECT
        self.sendall(b"\x05" + cmd + b"\x00\x01" + struct.pack(">H", dest_port) + addr)
        resp = self._readall(self, 4)
        if ord(resp[1:2]) != 0:
            raise socket.error(f"SOCKS5 connection failed: {resp}")
        a_typ = ord(self._readall(self, 1))
        if a_typ == 1:
            bind_addr = socket.inet_ntoa(self._readall(self, 4))
        elif a_typ == 3:
            domain_length = ord(self._readall(self, 1))
            bind_addr = self._readall(self, domain_length).decode('ascii')
        elif a_typ == 4:
            raise socket.error("IPv6 address type not handled")
        else:
            raise socket.error(f"Unknown address type {a_typ}")
        bind_port = struct.unpack(">H", self._readall(self, 2))[0]
        self.proxy_peername = (bind_addr, bind_port)

    def _negotiate_HTTP(self, dest_addr, dest_port):
        request = f"CONNECT {dest_addr}:{dest_port} HTTP/1.1\r\nHost: {dest_addr}:{dest_port}\r\n\r\n".encode()
        self.sendall(request)
        response = b""
        while True:
            ready = select.select([self], [], [], self.gettimeout())
            if not ready[0]:
                raise socket.timeout("HTTP proxy timeout")
            data = self.recv(4096)
            if not data:
                break
            response += data
            if b"\r\n\r\n" in response:
                break
        if not response.startswith(b"HTTP/1."):
            raise socket.error("Invalid HTTP proxy response")
        status_line = response.split(b"\r\n", 1)[0]
        if not status_line.endswith(b"200 OK"):
            raise socket.error(f"HTTP proxy returned non-200 code: {status_line}")

    def _negotiate(self, dest_addr, dest_port):
        proxy_type, proxy_host, proxy_port, rdns, username, password = self.proxy
        if proxy_type == socks.PROXY_TYPE_SOCKS4:
            self.connect((proxy_host, proxy_port))
            self._negotiate_SOCKS4(dest_addr, dest_port)
        elif proxy_type == socks.PROXY_TYPE_SOCKS5:
            self.connect((proxy_host, proxy_port))
            self._negotiate_SOCKS5(dest_addr, dest_port)
        elif proxy_type == socks.PROXY_TYPE_HTTP:
            self.connect((proxy_host, proxy_port))
            self._negotiate_HTTP(dest_addr, dest_port)
        else:
            raise socket.error(f"Unsupported proxy type: {proxy_type}")
