    def __init__(self, ip: str = '127.0.0.1', port: int = 6801, source: int = 0, target: int = 0, outputcls: Type[Any] = None) -> None:
        super().__init__(ip, port)
        self.source = source
        self.target = target
        self.outputcls = outputcls

    def send(self, x: Any) -> int:
        flags = 0
        header = struct.pack('>IIII', len(x), flags, self.source, self.target)
        packet = header + x
        return super().send(packet)

    def recv(self, x: int = 65535) -> Optional[Any]:
        raw_data = super().recv(x)
        if raw_data is None:
            return None
        if len(raw_data) < 16:
            return None
        length, flags, source, target = struct.unpack('>IIII', raw_data[:16])
        payload = raw_data[16:]
        if self.outputcls:
            return self.outputcls(payload)
        return payload
