import socket
import pickle
import os

class CSocket:
    CSPROTO_HEADER = 4
    CSPROTO_MAGIC = b'CSPRO'

    def __init__(self, sock: str, timeout: int = -1) -> None:
        self.__csock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
        self.__deftout = 30.0 if timeout == -1 else float(timeout)
        self.settimeout(self.__deftout)
        self.__csock.connect(sock)

    def __del__(self) -> None:
        self.close()

    def send(self, msg: Any, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        data = pickle.dumps(msg)
        header = self.CSPROTO_MAGIC + len(data).to_bytes(self.CSPROTO_HEADER, byteorder='big')
        full_data = header + data

        if timeout is not None:
            self.settimeout(timeout)
        elif nonblocking:
            self.settimeout(0.0)

        try:
            self.__csock.sendall(full_data)
            return self.receive(self.__csock, nonblocking, timeout)
        except socket.error as e:
            raise RuntimeError(f"Socket communication failed: {e}")

    def settimeout(self, timeout: float) -> None:
        self.__csock.settimeout(timeout)

    def close(self) -> None:
        try:
            self.__csock.shutdown(socket.SHUT_RDWR)
        except (OSError, socket.error):
            pass
        finally:
            self.__csock.close()
            try:
                os.remove(self.__csock.getpeername())
            except Exception:
                pass

    @staticmethod
    def convert(m: Any) -> Union[str, bool, int, float, list, dict, set]:
        if isinstance(m, (str, bool, int, float, list, dict, set)):
            return m
        elif hasattr(m, '__dict__'):
            return vars(m)
        else:
            return str(m)

    @staticmethod
    def receive(sock: socket.socket, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        if timeout is not None:
            sock.settimeout(timeout)
        elif nonblocking:
            sock.settimeout(0.0)

        try:
            header = sock.recv(CSocket.CSPROTO_HEADER + len(CSocket.CSPROTO_MAGIC))
            if not header or header[:len(CSocket.CSPROTO_MAGIC)] != CSocket.CSPROTO_MAGIC:
                raise ValueError("Invalid protocol header")
            length = int.from_bytes(header[len(CSocket.CSPROTO_MAGIC):], byteorder='big')
            payload = b''
            while len(payload) < length:
                chunk = sock.recv(length - len(payload))
                if not chunk:
                    raise ConnectionError("Connection closed before message received")
                payload += chunk
            return pickle.loads(payload)
        except socket.timeout:
            raise TimeoutError("Receive operation timed out")
        except socket.error as e:
            raise RuntimeError(f"Socket receive error: {e}")
