import socket
import struct
import time
from typing import List, Dict, Union
import hashlib

class CMPPBaseRequestInstance:
    def __init__(self):
        self.total_length = 0
        self.command_id = 0
        self.sequence_id = 0

    def to_bytes(self) -> bytes:
        return b''

class CMPPConnectRequest(CMPPBaseRequestInstance):
    def __init__(self, sp_id: str, sp_secret: str, sequence_id: int):
        super().__init__()
        self.total_length = 12 + len(sp_id) * 2 + 16 + 4
        self.command_id = 0x00000002
        self.sequence_id = sequence_id
        self.sp_id = sp_id.encode('gbk')
        self.authenticator_md5 = self._generate_authenticator_md5(sp_secret)

    def _generate_authenticator_md5(self, sp_secret: str) -> bytes:
        timestamp = (int(time.time()) - 20828).to_bytes(4, 'little')  # CMPP uses a specific epoch
        secret_bytes = sp_secret.encode('gbk')
        md5_hash = hashlib.md5(secret_bytes + timestamp).digest()
        return md5_hash

    def to_bytes(self) -> bytes:
        header = struct.pack('>IIBBHI', self.total_length, self.command_id, 0, 0, 0, self.sequence_id)
        body = self.sp_id + b'\x00' * (10 - len(self.sp_id)) + self.authenticator_md5 + b'\x00' * 4
        return header + body

class CMPPDeliverRequest(CMPPBaseRequestInstance):
    def __init__(self, mobiles: List[str], content: str, src_id: str, service_id: str, sequence_id: int):
        super().__init__()
        self.total_length = 12 + 2 + sum(len(mobile) for mobile in mobiles) + len(content.encode('gbk')) + 2 + len(src_id.encode('gbk')) + len(service_id.encode('gbk'))
        self.command_id = 0x00000004
        self.sequence_id = sequence_id
        self.dest_mobiles = [mobile.encode('ascii') for mobile in mobiles]
        self.content = content.encode('gbk')
        self.src_id = src_id.encode('gbk')
        self.service_id = service_id.encode('gbk')

    def to_bytes(self) -> bytes:
        header = struct.pack('>IIBBHI', self.total_length, self.command_id, 0, 0, 0, self.sequence_id)
        mobile_count = len(self.dest_mobiles)
        mobiles_bytes = b''.join([struct.pack('H', len(mobile)) + mobile for mobile in self.dest_mobiles])
        body = struct.pack('H', mobile_count) + mobiles_bytes + self.content + b'\x00' + self.src_id + self.service_id
        return header + body

class CMPPClient:
    def __init__(self, host: str, port: int, sp_id: str, sp_secret: str, src_id: str, service_id: str) -> None:
        self.ip = host
        self.port = port
        self.sp_id = sp_id
        self.sp_secret = sp_secret
        self.src_id = src_id
        self.service_id = service_id
        self._sequence_id = 0
        self._is_connect = False
        self._times = 3
        self.__socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self._connect()

    @property
    def sequence_id(self) -> int:
        self._sequence_id += 1
        return self._sequence_id

    def _connect(self) -> None:
        for i in range(self._times):
            try:
                self.__socket.connect((self.ip, self.port))
                self._is_connect = True
                self._cmpp_connect()
                break
            except Exception as e:
                print(f"Connection failed, retrying {i + 1}/{self._times}: {e}")
                time.sleep(2 ** i)
        else:
            raise ConnectionError("Failed to connect after multiple attempts")

    def send(self, instance: Union[CMPPBaseRequestInstance, bytes]) -> None:
        if isinstance(instance, CMPPBaseRequestInstance):
            data = instance.to_bytes()
        else:
            data = instance
        self.__socket.sendall(data)

    def recv(self) -> tuple:
        while True:
            try:
                total_length = struct.unpack('>I', self.__socket.recv(4))[0]
                remaining = total_length - 4
                data = b''
                while remaining > 0:
                    chunk = self.__socket.recv(remaining)
                    if not chunk:
                        break
                    data += chunk
                    remaining -= len(chunk)
                if len(data) < total_length - 4:
                    continue
                command_id = struct.unpack('>I', data[:4])[0]
                sequence_id = struct.unpack('>I', data[8:12])[0]
                return command_id, sequence_id, data[12:]
            except socket.timeout:
                continue
            except Exception as e:
                print(f"Error receiving response: {e}")
                return 0, 0, b''

    def close(self) -> None:
        if self._is_connect:
            self.__socket.close()
            self._is_connect = False

    def _cmpp_connect(self) -> None:
        request = CMPPConnectRequest(self.sp_id, self.sp_secret, self.sequence_id)
        self.send(request)
        cmd, seq, resp = self.recv()
        if cmd != 0x00000003 or seq != request.sequence_id:
            raise ConnectionRefusedError("Authentication failed")

    def _cmpp_send_sms(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict) -> None:
        content = f"{sign_name}{template_code}:{template_param}"
        request = CMPPDeliverRequest(dest, content, self.src_id, self.service_id, self.sequence_id)
        self.send(request)
        cmd, seq, resp = self.recv()
        if cmd != 0x00000005 or seq != request.sequence_id:
            raise RuntimeError("Failed to send SMS")

    def send_sms(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict) -> None:
        if not self._is_connect:
            self._connect()
        try:
            self._cmpp_send_sms(dest, sign_name, template_code, template_param)
        except Exception as e:
            print(f"Error sending SMS: {e}")
            self.close()
            self._connect()
            self._cmpp_send_sms(dest, sign_name, template_code, template_param)
