import socket
import select
import threading
import queue
import logging


class ThreadedTCPClientSocket(threading.Thread):
    def __init__(self, ip, port, listener_q, remote_q, config, log):
        super().__init__()
        self.ip = ip
        self.port = port
        self.listener_q = listener_q
        self.remote_q = remote_q
        self.config = config
        self.logger = log
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.setblocking(0)
        self._stop_event = threading.Event()

    def connect(self):
        try:
            self.sock.connect((self.ip, self.port))
            local_port = self.sock.getsockname()[1]
            self.logger.info(f"Connected to {self.ip}:{self.port}, local port: {local_port}")
            return local_port
        except Exception as e:
            self.logger.error(f"Connection failed: {e}")
            return None

    def run(self):
        if not self.connect():
            self.logger.error("Failed to connect to the server.")
            return

        while not self._stop_event.is_set():
            try:
                rlist, wlist, xlist = select.select([self.sock], [self.sock], [self.sock], 0.1)

                if self.sock in rlist:
                    data = self.sock.recv(4096)
                    if data:
                        self.listener_q.put(data)
                    else:
                        self.logger.info("Server closed the connection.")
                        break

                if self.sock in wlist and not self.remote_q.empty():
                    try:
                        data = self.remote_q.get_nowait()
                        self.sock.sendall(data)
                    except queue.Empty:
                        pass

                if self.sock in xlist:
                    self.logger.error("Exceptional condition on socket.")
                    break

            except Exception as e:
                self.logger.error(f"Error during communication: {e}")
                break

        self.sock.close()
        self.logger.info("Client socket closed.")

    def stop(self):
        self._stop_event.set()
