import httpx
import chardet
from typing import Union, Tuple, Dict
from pathlib import Path

def fetch_url_content(url: Union[str, Path], logger) -> Tuple[int, Dict]:
    headers = {
        "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36"
    }

    try:
        with httpx.Client() as client:
            response = client.get(str(url), headers=headers, timeout=10)
            response.raise_for_status()
    except httpx.RequestError as e:
        logger.error(f"Request error for {url}: {e}")
        return -1, {}
    except httpx.HTTPStatusError as e:
        logger.error(f"HTTP status error for {url}: {e.response.status_code} - {e}")
        return e.response.status_code, {}

    try:
        encoding = chardet.detect(response.content)['encoding']
        content = response.content.decode(encoding or 'utf-8')
    except Exception as e:
