        if not self.outbuffer:
            return 0
        with self._write_lock:
            try:
                bytes_sent = self.sock.send(self.outbuffer)
                self.outbuffer = self.outbuffer[bytes_sent:]
                return bytes_sent
            except Exception:
                self.close()
                return 0

    def recv(self) -> None:
        with self._read_lock:
            try:
                data = self.sock.recv(4096)
                if not data:
                    self.close()
                    return
                self.inbuffer += data
                self.handle_read(data)
            except Exception:
                self.close()

    def push(self, data: bytes) -> None:
        with self._write_lock:
            self.outbuffer += data

    def handle_read(self, data: bytes) -> None:
        raise NotImplementedError("Subclasses must implement this method")

    def process_message(self) -> None:
        raise NotImplementedError("Subclasses must implement this method")

    def ready_to_read(self) -> bool:
        return True

    def ready_to_write(self) -> bool:
        return True

    def close(self) -> None:
        with self._read_lock:
            with self._write_lock:
                self.connected = False
                if hasattr(self.sock, 'fileno') and self.sock.fileno() != -1:
