        self._send_string(json_data.decode('utf-8'))

    def _get_json_dict(self) -> Dict[str, Any]:
        data = self._get_data()
        if data:
            return json.loads(data)
        return {}

    def _get_data(self) -> Optional[str]:
        length_bytes = self.connection.recv(4)
        if not length_bytes:
            return None
        length = int.from_bytes(length_bytes, byteorder='big')
        data = ''
        while len(data) < length:
            packet = self.connection.recv(length - len(data)).decode('utf-8')
            if not packet:
                return None
            data += packet
        return data

    def _send_string(self, string: str) -> None:
        encoded_string = string.encode('utf-8')
        length = len(encoded_string)
        self.connection.sendall(length.to_bytes(4, byteorder='big'))
        self.connection.sendall(encoded_string)

    def close(self) -> None:
        self.connection.close()
