import socket
import threading
from queue import Queue
from typing import Tuple

class BaseSocket:
    def __init__(self, host: str, port: int, debug: bool = False) -> None:
        self.serversocket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.serversocket.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self.serversocket.bind((host, port))
        self.serversocket.listen(5)
        self.queue = Queue()
        self.t = threading.Thread(target=self.run)
        self.debug = debug
        self.disconnect = False
        self.t.start()

    @staticmethod
    def recvLine(sock: socket.socket) -> Tuple[bool, str]:
        buffer = b''
        while True:
            try:
                data = sock.recv(1)
                if not data:
                    return (False, '')
                buffer += data
                if buffer.endswith(b'\n'):
                    return (True, buffer.decode().rstrip('\n'))
            except socket.error:
                return (False, '')

    @staticmethod
    def recvAll(sock: socket.socket, l: str) -> str:
        length = int(l)
        buffer = b''
        while len(buffer) < length:
            try:
                chunk = sock.recv(length - len(buffer))
                if not chunk:
                    return ''
                buffer += chunk
            except socket.error:
                return ''
        return buffer.decode()

    def run(self) -> None:
        while not self.disconnect:
            try:
                clientsock, addr = self.serversocket.accept()
                if self.debug:
                    print(f"Connection from {addr}")
                while True:
                    success, line = self.recvLine(clientsock)
                    if not success or not line:
                        break
                    if ':' in line:
                        length_str, data = line.split(':', 1)
                        received_data = self.recvAll(clientsock, length_str)
                        if received_data:
                            self.queue.put(data + received_data)
                clientsock.close()
            except Exception as e:
                if self.debug:
                    print(f"Error: {e}")
                break

    def update(self) -> None:
        while not self.queue.empty():
            data = self.queue.get()
            self.main(data)

    def main(self, datas: str) -> None:
        pass

    def __del__(self) -> None:
        self.disconnect = True
        self.serversocket.close()
        if self.t.is_alive():
            self.t.join()
