import socket
import select
import threading
from typing import Generator, Optional

class HTTPRequest:
    def __init__(self):
        self.method = ""
        self.path = ""
        self.http_version = ""
        self.headers = {}
        self.body = b""

    @classmethod
    def parse(cls, data: bytes) -> "HTTPRequest":
        request = cls()
        lines = data.split(b"\r\n")
        if not lines:
            return request
        first_line = lines[0].decode("utf-8").strip().split()
        if len(first_line) >= 3:
            request.method, request.path, request.http_version = first_line[:3]
        for line in lines[1:]:
            if b":" in line:
                key, value = line.split(b":", 1)
                request.headers[key.strip().decode("utf-8")] = value.strip()
        if len(lines) > 1 and lines[-1] == b"":
            request.body = b"\r\n".join(lines[lines.index(b"") + 1:])
        return request


