        self._running = False

    @property
    def addresses(self) -> list[str]:
        try:
            addrs = []
            hostname = socket.gethostname()
            ip_addrs = socket.gethostbyname_ex(hostname)[2]
            for ip in ip_addrs:
                addrs.append(ip)
            return addrs
        except Exception:
            return ["127.0.0.1"]

    def urls(self) -> Generator[str, None, None]:
        for addr in self.addresses:
            yield f"http://{addr}:{self.port}"

