        self.conn = conn
        self.bufsize = bufsize

class SendEvent(WaitableEvent):
    def __init__(self, conn, data, sendall=False):
        super().__init__()
        self.conn = conn
        self.data = data
        self.sendall = sendall
        self.sent = 0

