                    conn, addr = self.sock.accept()
                    self.onAccept(conn, addr)
                    threading.Thread(target=self._handle_client, args=(conn, addr)).start()
                except Exception as e:
                    self.onAcceptError(e)
        except Exception as e:
            self.onStartFail(e)

    def Stop(self):
        if self.sock:
            try:
                self.sock.shutdown(socket.SHUT_RDWR)
                self.sock.close()
            except:
                pass
        self.running = False
        self.stop_flag.set()
        self.onStop()

    def _handle_client(self, sock, addr):
        try:
            while self.running:
                data = sock.recv(4096)
                if not data:
                    break
                self.onData(sock, addr, data)
        except Exception as e:
            self.onRecvError(sock, addr, e)
        finally:
            try:
                sock.close()
            except:
                pass

    def onAccept(self, sock, addr):
        pass

