import socket
import select

class TCPForwardServer:
    def __init__(self, lhost: str, lport: int, rdev: "Device", rport: int) -> None:
        self.input_list = []
        self.channel = {}
        self._server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self._server.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self._server.bind((lhost, lport))
        self._server.listen(5)
        self._rdev = rdev
        self._rport = rport
        self.BUFFER_SIZE = 4096
        self.DELAY = 0.01
        self.input_list.append(self._server)

    def main_loop(self) -> None:
        while True:
            readable, _, exceptional = select.select(self.input_list, [], self.input_list, self.DELAY)
            for s in readable:
                if s is self._server:
                    self.on_accept()
                elif s in self.channel:
                    self.on_recv(s)
            for s in exceptional:
                self.on_close(s)

    def on_accept(self) -> None:
        client, addr = self._server.accept()
        print(f"Accepted connection from {addr}")
        try:
            device_socket = socket.create_connection((self._rdev.address, self._rport))
        except Exception as e:
            print(f"Failed to connect to remote device: {e}")
            client.close()
            return
        self.input_list.append(client)
        self.input_list.append(device_socket)
        self.channel[client] = device_socket
        self.channel[device_socket] = client

    def on_close(self, sock: socket.socket) -> None:
        print(f"Closing connection {sock.getpeername()}")
        partner = self.channel.get(sock)
        if partner:
            self.input_list.remove(partner)
            self.input_list.remove(sock)
            partner.close()
            del self.channel[partner]
            del self.channel[sock]

    def on_recv(self, sock: socket.socket) -> None:
