import socket
import selectors

class Socket:
    def __init__(self, sock):
        self._sock = sock

    def recv(self, maxbytes):
        return ReadSocket(self._sock, maxbytes)

    def send(self, data):
        return WriteSocket(self._sock, data)

    def accept(self):
        return AcceptSocket(self._sock)

    def __getattr__(self, name):
        return getattr(self._sock, name)


class Operation:
    def handle_yield(self, sched, task):
        raise NotImplementedError

    def handle_resume(self, sched, task):
        raise NotImplementedError


class ReadSocket(Operation):
    def __init__(self, sock, nbytes):
        self.sock = sock
        self.nbytes = nbytes
        self._result = None

    def handle_yield(self, sched, task):
        sched.register_read(self.sock, task)

    def handle_resume(self, sched, task, data):
        self._result = data
        task.resume(data)

    @property
    def result(self):
        return self._result


class WriteSocket(Operation):
    def __init__(self, sock, data):
        self.sock = sock
        self.data = data
        self._result = None

    def handle_yield(self, sched, task):
        sched.register_write(self.sock, task)

    def handle_resume(self, sched, task, bytes_sent):
        self._result = bytes_sent
        task.resume(bytes_sent)

    @property
    def result(self):
        return self._result


class AcceptSocket(Operation):
    def __init__(self, sock):
        self.sock = sock
        self._result = None

    def handle_yield(self, sched, task):
        sched.register_accept(self.sock, task)

    def handle_resume(self, sched, task, conn):
        self._result = Socket(conn)
        task.resume(Socket(conn))

    @property
    def result(self):
        return self._result


class Task:
    def __init__(self, gen):
        self.gen = gen

    def resume(self, value=None):
        try:
            self.gen.send(value)
        except StopIteration:
            pass


class Scheduler:
    def __init__(self):
        self._readers = {}
        self._writers = {}
        self._acceptors = {}
        self._tasks = []

    def register_read(self, sock, task):
        self._readers[sock] = task
        self._tasks.append(task)

    def register_write(self, sock, task):
        self._writers[sock] = task
        self._tasks.append(task)

    def register_accept(self, sock, task):
        self._acceptors[sock] = task
        self._tasks.append(task)

    def run(self):
        sel = selectors.DefaultSelector()
        for sock in self._readers:
            sel.register(sock, selectors.EVENT_READ)
        for sock in self._writers:
            sel.register(sock, selectors.EVENT_WRITE)
        for sock in self._acceptors:
            sel.register(sock, selectors.EVENT_READ)

        while self._tasks:
            events = sel.select(timeout=1)
            for key, mask in events:
                sock = key.fileobj
                if key.fd in self._readers:
                    conn, addr = sock.accept()
                    task = self._readers[sock]
                    task.handle_resume(self, task, conn)
                elif key.fd in self._writers:
                    task = self._writers[sock]
                    bytes_sent = sock.send(b"data")
                    task.handle_resume(self, task, bytes_sent)
                elif key.fd in self._acceptors:
                    conn, addr = sock.accept()
                    task = self._acceptors[sock]
                    task.handle_resume(self, task, conn)

            new_tasks = []
            for task in self._tasks:
                try:
                    next(task.gen)
                except StopIteration:
                    continue
                new_tasks.append(task)
            self._tasks = new_tasks


class EchoServer:
    def __init__(self, addr, sched):
        self.addr = addr
        self.sched = sched

    def server_loop(self):
        with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as sock:
            sock.setblocking(False)
            sock.bind(self.addr)
            sock.listen(5)
            while True:
                client = yield sock.accept()
                self.sched.register_read(client.result._sock, Task(self.client_handler(client)))

    def client_handler(self, client):
        while True:
            data = yield client.recv(4096)
            if not data.result:
                break
            yield client.send(data.result)
        client._sock.close()


def main():
    scheduler = Scheduler()
    echo_server = EchoServer(('localhost', 8080), scheduler)
    scheduler.register_read(echo_server.addr[0], Task(echo_server.server_loop()))
    scheduler.run()


if __name__ == "__main__":
    main()
