import socketserver
import threading
import socket

class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self) -> bytes:
        try:
            size_bytes = self.request.recv(2)
            if not size_bytes:
                return b''
            data_size = int.from_bytes(size_bytes, byteorder='big')
            payload = self.request.recv(data_size)
            if len(payload) != data_size:
                raise ValueError("Received incomplete data")
            return payload
        except Exception as e:
            print(f"Error receiving data: {e}")
            return b''

    def send_data(self, data: bytes) -> None:
        try:
            size_bytes = len(data).to_bytes(2, byteorder='big')
            self.request.sendall(size_bytes + data)
        except Exception as e:
            print(f"Error sending data: {e}")

    def handle(self):
        while True:
            data = self.get_data()
            if not data:
                break
            # Echo the received data back to the client
            self.send_data(data)

