        if self.use_tcp:
            tcp_server = socketserver.TCPServer(('0.0.0.0', self.port), TCPRequestHandler)
            tcp_thread = threading.Thread(target=tcp_server.serve_forever)
            tcp_thread.daemon = True
            tcp_thread.start()
