import socket
import threading

class UDPRelay:
    def __init__(self, config: dict, dns_resolver: object, is_local: bool) -> None:
        self._config = config
        self._dns_resolver = dns_resolver
        self._is_local = is_local
        self._sockets = set()
        self._server_socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self._closed = False
        self._listen_addr = config.get('listen_address', '0.0.0.0')
        self._listen_port = config.get('listen_port', 53)

        try:
            self._server_socket.bind((self._listen_addr, self._listen_port))
        except Exception as e:
            raise RuntimeError(f"Failed to bind server socket: {e}")

    def _handle_server(self) -> None:
        while not self._closed:
            try:
                data, addr = self._server_socket.recvfrom(65535)
                if self._is_local:
                    remote_host = self._config.get('remote_address')
                    remote_port = self._config.get('remote_port')
                    self._dns_resolver.resolve(remote_host, self._handle_server_dns_resolved, (data, addr, remote_port))
                else:
                    local_host = self._config.get('local_address')
                    local_port = self._config.get('local_port')
                    self._dns_resolver.resolve(local_host, self._handle_server_dns_resolved, (data, addr, local_port))
            except Exception:
                pass

    def _handle_server_dns_resolved(self, error: str, remote_addr: tuple, server_addr: str, params: tuple) -> None:
        if error:
            return
        data, original_addr, port = params
        sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self._sockets.add(sock)
        try:
            sock.sendto(data, (remote_addr[0], port))
        except Exception:
            pass

    def _handle_client(self, sock: socket.socket) -> None:
        while not self._closed:
            try:
                data, addr = sock.recvfrom(65535)
                self._server_socket.sendto(data, addr)
            except Exception:
                self._sockets.remove(sock)
                sock.close()
                break

    def close(self) -> None:
        self._closed = True
        for sock in self._sockets:
            sock.close()
        self._server_socket.close()

    def start(self) -> None:
        threading.Thread(target=self._handle_server, daemon=True).start()
        for sock in self._sockets:
            threading.Thread(target=self._handle_client, args=(sock,), daemon=True).start()
