            try:
                await self.ws.close()
            except ConnectionClosed:
                pass


class JSONBaseProvider:
    pass


class RPCResponse:
    def __init__(self, result=None, error=None):
        self.result = result
        self.error = error


class LegacyWebSocketProvider(JSONBaseProvider):
    _loop: ClassVar[Any] = asyncio.get_event_loop()

    def __init__(self, endpoint_uri: str, websocket_timeout: int = 10, **websocket_kwargs: Any):
        self.endpoint_uri = endpoint_uri
        self.websocket_timeout = websocket_timeout
        self.conn = PersistentWebSocket(endpoint_uri, **websocket_kwargs)

    async def coro_make_request(self, request_data: bytes) -> RPCResponse:
        async with self.conn as ws:
            try:
                await ws.send(request_data.decode('utf-8'))
                response = await asyncio.wait_for(ws.recv(), timeout=self.websocket_timeout)
                data = json.loads(response)
                if 'error' in data:
                    return RPCResponse(error=data['error'])
                else:
                    return RPCResponse(result=data.get('result'))
            except Exception as e:
                return RPCResponse(error=str(e))

    def make_request(self, method: RPCEndpoint, params: Any) -> RPCResponse:
        request_id = hash((method, params))
        request_data = json.dumps({
            "jsonrpc": "2.0",
            "method": method,
            "params": params,
            "id": request_id
        }).encode('utf-8')
        return self._loop.run_until_complete(self.coro_make_request(request_data))

    def make_batch_request(
        self, requests: List[Tuple[RPCEndpoint, Any]]
    ) -> List[RPCResponse]:
        batch = [
            {
                "jsonrpc": "2.0",
                "method": method,
                "params": params,
                "id": hash((method, params))
            }
            for method, params in requests
        ]
        batch_data = json.dumps(batch).encode('utf-8')
        return [self._loop.run_until_complete(self.coro_make_request(json.dumps(req).encode('utf-8'))) for req in batch]
