from queue import Queue, Empty


class Protocol:
    def __init__(self):
        self.next_id = 1
        self.commands = {}
        self.notifications = {}
        self.url = None
        self.on_open = None
        self.on_close = None
        self.socket = None
        self.main_thread_queue = Queue()
        self.lock = threading.Lock()

    def connect(self, url, on_open=None, on_close=None):
        self.url = url
        self.on_open = on_open
        self.on_close = on_close

        thread = threading.Thread(target=self.thread_callback)
        thread.start()

    def thread_callback(self):
        self.socket = websocket.WebSocketApp(
            self.url,
            on_message=self.message_callback,
            on_open=self.open_callback,
            on_close=self.close_callback
        )
        self.socket.run_forever()

    def send(self, command, callback=None, options=None):
        command_id = self.next_id
        self.next_id += 1

        payload = {"id": command_id, "command": command}
        if options:
            payload["options"] = options

        message = json.dumps(payload)
        self.socket.send(message)

        if callback:
            with self.lock:
                self.commands[command_id] = callback

    def subscribe(self, notification, callback):
        with self.lock:
            self.notifications[notification] = callback

    def unsubscribe(self, notification):
        with self.lock:
            if notification in self.notifications:
                del self.notifications[notification]

    def message_callback(self, ws, message):
        try:
            data = json.loads(message)
        except json.JSONDecodeError:
            return

        command_id = data.get("id")
        if command_id and command_id in self.commands:
            callback = self.commands.get(command_id)
            if callback:
                self.to_main_thread(callback, (data,))
                with self.lock:
                    if command_id in self.commands:
                        del self.commands[command_id]
        else:
            notification = data.get("notification")
            if notification and notification in self.notifications:
                callback = self.notifications.get(notification)
                if callback:
                    self.to_main_thread(callback, (data,))

    def open_callback(self, ws):
        self.to_main_thread(self.on_open, () if self.on_open else None)

    def close_callback(self, ws):
        self.to_main_thread(self.on_close, () if self.on_close else None)

    def to_main_thread(self, f, args):
        if threading.current_thread() != threading.main_thread():
            self.main_thread_queue.put((f, args))
        else:
            while not self.main_thread_queue.empty():
                func, args = self.main_thread_queue.get()
                func(*args) if args else func()

    def run_main_loop(self):
        while True:
            try:
                func, args = self.main_thread_queue.get(timeout=0.1)
                func(*args) if args else func()
            except Empty:
                pass
