        try:
            msg_data = json.loads(message)
            if self.debug_mode:
                print(f"Control message received: {msg_data}")
            if msg_data.get("type") == "parameter_response":
                req_id = msg_data["request_id"]
                with self.lock:
                    if req_id in self.pending_requests:
                        callback_info = self.pending_requests.pop(req_id)
                        callback_info["callback"](msg_data["value"], msg_data.get("error"))
        except Exception as e:
            if self.debug_mode:
                print(f"Error processing control message: {e}")

    def on_data_message(self, ws: websocket.WebSocketApp, message: str) -> None:
        if self.debug_mode:
            print(f"Data message received: {message}")

    def on_error(self, ws: websocket.WebSocketApp, error: Exception) -> None:
        if self.debug_mode:
            print(f"WebSocket error: {error}")

    def on_close(self, ws: websocket.WebSocketApp, close_status_code: int, close_msg: str) -> None:
        if self.debug_mode:
            print(f"Connection closed: status code {close_status_code}, message: {close_msg}")
        self.is_running = False

    def on_control_open(self, ws: websocket.WebSocketApp) -> None:
        if self.debug_mode:
            print("Control connection opened")
        self.connection_established.set()

    def on_data_open(self, ws: websocket.WebSocketApp) -> None:
        if self.debug_mode:
            print("Data connection opened")

    def set_parameter(self, parameter: str, value: Any) -> None:
        req_id = self._generate_request_id()
        message = {
            "type": "set_parameter",
            "parameter": parameter,
            "value": value,
            "request_id": req_id
        }
        self._control_ws.send(json.dumps(message))

    def get_parameter(self, parameter: str) -> Optional[Any]:
        result = [None]
        error = [None]

        def callback(value, err):
            result[0] = value
            error[0] = err

        req_id = self._generate_request_id()
        self.pending_requests[req_id] = {"callback": callback}
        message = {
            "type": "get_parameter",
            "parameter": parameter,
            "request_id": req_id
        }
        self._control_ws.send(json.dumps(message))

        start_time = time.time()
        while result[0] is None and error[0] is None and time.time() - start_time < 5:
            time.sleep(0.1)

        if error[0]:
            raise Exception(f"Error retrieving parameter: {error[0]}")
        return result[0]

    def call_method(self, method: str, args: Optional[List] = None, kwargs: Optional[dict] = None) -> None:
        req_id = self._generate_request_id()
        message = {
            "type": "call_method",
            "method": method,
            "args": args or [],
            "kwargs": kwargs or {},
            "request_id": req_id
        }
        self._control_ws.send(json.dumps(message))

    def shutdown(self) -> None:
        self.is_running = False
        if self._control_ws:
            self._control_ws.close()
        if self._data_ws:
            self._data_ws.close()
        self.connection_established.clear()

    def _generate_request_id(self) -> int:
        with self.lock:
            self.request_counter += 1
            return self.request_counter
