def writeScreenCapOutputFrames(participant_id, frames):
    for i, frame in enumerate(frames):
        with open(f"{participant_id}_frame_{i}.jpg", "wb") as f:
            f.write(frame)

def closeScreenCapOutVideo(participant_id):
    pass

class WebSocketHandler(tornado.websocket.WebSocketHandler):
    def open(self):
        self.participant_id = len(participants) + 1
        participant_positions[self.participant_id] = 0
        newParticipant(self.participant_id)

    def on_message(self, message):
        msg = tornado.escape.json_decode(message)
        if msg["msgID"] == "requestFrame":
            pos = participant_positions[self.participant_id]
            if pos < len(participants[self.participant_id]["frames"]):
                sendVideoFrame(
                    self.participant_id,
                    participants[self.participant_id]["frames"][pos],
                    self
                )
                participant_positions[self.participant_id] += 1
        elif msg["msgID"] == "sendData":
            writeDataToCSV(self.participant_id, msg["data"])
        elif msg["msgID"] == "endSession":
            sendVideoEnd(self.participant_id, self)
            closeScreenCapOutVideo(self.participant_id)

    def on_close(self):
        pass

    def on_error(self):
        pass

class Application(tornado.web.Application):
    def __init__(self):
        handlers = [
            (r"/websocket", WebSocketHandler),
            (r"/(.*)", tornado.web.StaticFileHandler, {"path": "static"}),
        ]
        settings = {
            "template_path": "templates",
        }
        super().__init__(handlers, **settings)

if __name__ == "__main__":
    app = Application()
    app.listen(8888)
    tornado.ioloop.IOLoop.current().start()
