import zmq
import threading
import time
from abc import ABC, abstractmethod


class RpcClient(ABC):
    def __init__(self) -> None:
        self._context = zmq.Context()
        self._socket_sub = self._context.socket(zmq.SUB)
        self._socket_sub.setsockopt(zmq.LINGER, 0)
        self._socket_sub.setsockopt(zmq.CONFLATE, 1)
        self._socket_sub.setsockopt(zmq.RCVTIMEO, 1000)
        self._active = False
        self._thread = threading.Thread(target=self.run)
        self._lock = threading.Lock()
        self._last_received_ping = 0.0

    def start(self, sub_address: str) -> None:
        with self._lock:
            if not self._active:
                self._socket_sub.connect(sub_address)
                self._active = True
                self._thread.start()

    def stop(self) -> None:
        with self._lock:
            if self._active:
                self._active = False
                self._socket_sub.disconnect("inproc://dummy")
                self._socket_sub.close()
                self._context.term()

    def join(self) -> None:
        self._thread.join()

    def run(self) -> None:
        while self._active:
            try:
                topic = self._socket_sub.recv_string()
                data = self._socket_sub.recv_pyobj()
                self.callback(topic, data)
                if topic == "heartbeat":
                    self._last_received_ping = time.time()
            except zmq.Again:
                now = time.time()
                if now - self._last_received_ping > 5:
                    self.on_disconnected()
                continue

    @abstractmethod
    def callback(self, topic: str, data: any) -> None:
        pass

    def subscribe_topic(self, topic: str) -> None:
        self._socket_sub.setsockopt(zmq.SUBSCRIBE, topic.encode())

    def on_disconnected(self) -> None:
        print("Warning: Disconnected from publisher.")
