import zmq
import numpy as np
import time
import uuid
from collections import defaultdict, namedtuple

Response = namedtuple('Response', ['request_id', 'data'])

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.receiver = self.context.socket(zmq.SUB)
        self.identity = identity.encode() if identity else str(uuid.uuid4()).encode()
        self.request_id = 0
        self.timeout = timeout
        self.pending_request = set()

        self.sender.connect(f'tcp://{ip}:{port}')
        self.receiver.connect(f'tcp://{ip}:{port_out}')
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        self.sender.send_multipart([self.identity, str(self.request_id).encode(), str(msg_len).encode(), msg])
        self.pending_request.add(self.request_id)
        self.request_id += 1

    def _recv(self):
        if self.receiver.poll(self.timeout, zmq.POLLIN):
            parts = self.receiver.recv_multipart()
            return parts[0], parts[1], parts[2]
        raise TimeoutError("No response received within timeout period.")

    def _recv_ndarray(self):
        request_id, _, data = self._recv()
        data = np.frombuffer(data, dtype=np.float32)
        return Response(int(request_id.decode()), data)

    def encode(self, texts, blocking=True):
        if isinstance(texts, str):
            texts = [texts]
        msg = '\n'.join(texts).encode()
        msg_len = len(msg)
        self._send(msg, msg_len)
        if not blocking:
            return None
        return self._recv_ndarray().data

    def fetch(self, delay=0.0):
        while True:
            if not self.pending_request:
                break
            time.sleep(delay)
            try:
                yield self._recv_ndarray()
            except TimeoutError:
                continue

    def fetch_all(self, sort=True, concat=False):
        results = []
        for _ in range(len(self.pending_request)):
            try:
                res = self._recv_ndarray()
                results.append(res)
            except TimeoutError:
                pass

        if sort:
            results.sort(key=lambda x: x.request_id)
        if concat and all(isinstance(r.data, np.ndarray) for r in results):
            return np.vstack([r.data for r in results])
        return [r.data for r in results]
