import zmq
import numpy as np
import threading
from collections import deque
import zlib
import pickle

class NetGear:
    def __init__(self, address=None, port=None, protocol=None, pattern=0, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__msg_socket = None
        self.__subscriber_timeout = options.get("timeout", 1)
        self.__queue = deque()
        self.__thread = None

        if protocol is None:
            protocol = "tcp"
        if address is None:
            address = "*"
        if port is None:
            port = "5555"

        if self.__pattern == 2 and receive_mode:
            self.__msg_socket = self.__msg_context.socket(zmq.SUB)
            connect_str = f"{protocol}://{address}:{port}"
            self.__msg_socket.connect(connect_str)
            self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout * 1000)
            self.__msg_socket.setsockopt_string(zmq.SUBSCRIBE, "")
        elif self.__pattern == 2 and not receive_mode:
            self.__msg_socket = self.__msg_context.socket(zmq.PUB)
            bind_str = f"{protocol://*}:{port}"
            self.__msg_socket.bind(bind_str)

        if receive_mode:
            self.__thread = threading.Thread(target=self.__recv_handler)
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                message = self.__msg_socket.recv()
                frame = pickle.loads(zlib.decompress(message))
                self.__queue.append(frame)
            except zmq.Again:
                pass

    def recv(self):
        if self.__queue:
            return self.__queue.popleft()
        return None

    def send(self, frame, message=None):
        data = {"frame": frame.tolist(), "meta": message}
        compressed_data = zlib.compress(pickle.dumps(data))
        self.__msg_socket.send(compressed_data)

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread and self.__thread.is_alive():
            self.__thread.join()
        if self.__msg_socket:
            self.__msg_socket.close()
        self.__msg_context.term()
