class ZeroMQConsumer:
    def __init__(self, zmq_mode: str, detokenization_pub_port: int, shm_req_manager):
        self.context = zmq.asyncio.Context()
        self.recv_from_detokenization = self.context.socket(zmq.SUB)
        self.recv_from_detokenization.connect(f"tcp://localhost:{detokenization_pub_port}")
        self.recv_from_detokenization.setsockopt_string(zmq.SUBSCRIBE, "")

        self.shm_req_manager = shm_req_manager
        self.req_id_to_out_inf: Dict[int, ReqStatus] = {}
        self.recycle_event = asyncio.Event()

    async def handle_loop(self):
        while True:
            try:
                message = await self.recv_from_detokenization.recv_json()
                request_id = message.get("request_id")
                token_info = message.get("token_info")

                if request_id not in self.req_id_to_out_inf:
                    continue

                req_status = self.req_id_to_out_inf[request_id]
                for req in req_status.req_objs:
                    if req.id == request_id:
                        req.process_token(token_info)
                        if req.can_be_completed():
                            req.mark_processed()
                            self.recycle_event.set()
            except Exception as e:
                print(f"Error in handle_loop: {e}")

    async def recycle_resource_loop(self):
        while True:
            await self.recycle_event.wait()
            self.recycle_event.clear()
            to_remove = []
            for req_id, status in self.req_id_to_out_inf.items():
                if status.can_release():
                    self.shm_req_manager.release_resources(status.group_request_id)
                    to_remove.append(req_id)
