def server(channel_ip=None, channel_port=5555):
    context = zmq.Context()
    socket = context.socket(zmq.REP)
    if channel_ip:
        socket.bind(f"tcp://{channel_ip}:{channel_port}")
    else:
        socket.bind(f"tcp://*:{channel_port}")

    remaining_entries = ["task1", "task2", "task3"]  # Example task IDs
    running_workers = 0

    try:
        while True:
            message = socket.recv_string()
            if message == "READY":
                running_workers += 1
                if remaining_entries:
                    task_id = remaining_entries.pop(0)
                    socket.send_string(task_id)
                else:
                    socket.send_string("DONE")
            elif message.startswith("SUCCESS:"):
                _, task_id = message.split(":", 1)
                print(f"Task {task_id} succeeded")
                socket.send_string("ACK")
            elif message.startswith("FAILURE:"):
                _, task_id = message.split(":", 1)
                print(f"Task {task_id} failed")
                socket.send_string("NACK")
            else:
                socket.send_string("UNKNOWN")
    except KeyboardInterrupt:
        print("\nServer shutting down...")
        socket.close()
        context.term()

if __name__ == "__main__":
