class MockEstopServicer(estop_pb2_grpc.EstopServiceServicer):
    VALID_STOP_LEVEL = 1
    NAME_FOR_ENDPOINT_UNKNOWN = "unknown-endpoint"
    NAME_FOR_SERVER_ERROR = "server-error"
    STATUSES_THAT_DO_NOT_PROVIDE_CHALLENGE = {estop_pb2.EstopCheckInResponse.STATUS_INACTIVE,
                                              estop_pb2.EstopCheckInResponse.STATUS_RELEASED}
    _rpc_delay = 0
    _challenge = 0

