        self._rpc_delay = rpc_delay
        self._challenge = int(time.time())

    def RegisterEstopEndpoint(self, request, context):
        return estop_pb2.RegisterEstopEndpointResponse()

    def EstopCheckIn(self, request, context):
        if self._rpc_delay > 0:
            time.sleep(self._rpc_delay)
        response = estop_pb2.EstopCheckInResponse()
        if request.name == self.NAME_FOR_ENDPOINT_UNKNOWN:
            response.status = estop_pb2.EstopCheckInResponse.STATUS_UNKNOWN_ENDPOINT
        elif request.name == self.NAME_FOR_SERVER_ERROR:
            raise Exception("Server error triggered.")
        else:
            response.status = estop_pb2.EstopCheckInResponse.STATUS_ACTIVE
            if request.challenge != self._challenge and request.status not in self.STATUSES_THAT_DO_NOT_PROVIDE_CHALLENGE:
                response.status = estop_pb2.EstopCheckInResponse.STATUS_CHALLENGE_MISMATCH
            else:
                self._challenge += 1
        return response


