from concurrent import futures
from typing import Dict, Iterator, List, Optional
import asyncio
from google.protobuf import empty_pb2

class ModelResponse(grpc.GenericRpcHandler):
    def __init__(self, async_pipeline=None):
        self._stop_event = threading.Event()
        self.inference_pipeline = async_pipeline
        self.method_name_to_task = {
            "GeneratorReply": "inference",
            "GeneratorReplyStream": "stream_inference"
        }
        self.lock = threading.Lock()

    def Terminate(self, request, context):
        self._stop_event.set()
        return empty_pb2.Empty()

    def get_stop_event(self):
        return self._stop_event

    def GeneratorReply(self, request, context):
        task_methods = self._get_task_methods("GeneratorReply")
        result = task_methods["process"](request)
        return result

    def GeneratorReplyStream(self, request, context):
        task_methods = self._get_task_methods("GeneratorReplyStream")
        for response in task_methods["process_stream"](request):
            yield response

    def _get_task_methods(self, method_name):
        task_type = self.method_name_to_task[method_name]
        return {"process": self._process_request, "process_stream": self._process_stream}

    def _process_request(self, request):
        with self.lock:
            return self.inference_pipeline.run(request)

    def _process_stream(self, request):
        with self.lock:
            for chunk in self.inference_pipeline.stream_run(request):
                yield chunk

    def service_name(self):
        return "ModelService"

    def handle_rpc(self, method_name, handler_info, request, context):
        if self._stop_event.is_set():
            context.abort(grpc.StatusCode.UNAVAILABLE, "Server is shutting down")
            return
        if method_name == "/ModelService/GeneratorReply":
            return self.GeneratorReply(request, context)
        elif method_name == "/ModelService/GeneratorReplyStream":
            return self.GeneratorReplyStream(request, context)
        elif method_name == "/ModelService/Terminate":
            return self.Terminate(request, context)
        else:
            context.abort(grpc.StatusCode.UNIMPLEMENTED, "Method not implemented")

def _do_serve(service_impl: ModelResponse, port: int, interceptors: Optional[List[grpc.ServerInterceptor]] = None) -> None:
    server = grpc.server(futures.ThreadPoolExecutor(max_workers=10), interceptors=interceptors)
    grpc.add_generic_rpc_handlers(server, (service_impl,))
    server.add_insecure_port(f"[::]:{port}")
    server.start()
    try:
        while not service_impl.get_stop_event().is_set():
            service_impl.get_stop_event().wait(1)
    except KeyboardInterrupt:
        pass
    finally:
        server.stop(None)
