import httpx
import hmac
import hashlib
import asyncio
from typing import Dict, Any, Optional
from dataclasses import dataclass


@dataclass
class ExternalActionResponse:
    success: bool
    data: Optional[Dict[str, Any]] = None
    error: Optional[str] = None


class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(
        self,
        payload: Dict[str, Any],
        signature_secret: str,
        additional_payload_values: Dict[str, Any] = {},
        additional_headers: Dict[str, str] = {},
        transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)
    ) -> ExternalActionResponse:
        combined_payload = {**payload, **additional_payload_values}
        encoded_payload = bytes(str(combined_payload), 'utf-8')
        signature = self._encode_payload(encoded_payload, signature_secret)

        headers = {
            "Content-Type": "application/json",
            "X-Signature": signature,
            **additional_headers
        }

        async with httpx.AsyncClient(transport=transport) as client:
            try:
                response = await client.post(self.url, json=combined_payload, headers=headers, timeout=10)
                response.raise_for_status()
                return self._validate_response(response.json())
            except httpx.HTTPStatusError as e:
                return ExternalActionResponse(success=False, error=f"API Error: {e}")
            except httpx.RequestError as e:
                return ExternalActionResponse(success=False, error=f"Network Error: {e}")

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        hmac_signature = hmac.new(signature_secret.encode(), payload, hashlib.sha256)
        return hmac_signature.hexdigest()

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        if "error" in response:
            return ExternalActionResponse(success=False, error=response["error"])
        elif "data" in response:
            return ExternalActionResponse(success=True, data=response["data"])
        else:
            return ExternalActionResponse(success=False, error="Invalid response format")
