            return response.status_code == 200
        except requests.RequestException:
            return False

    def create_async_task(self, data: Dict[str, Any], scraper_name: Optional[str] = None) -> dict:
        endpoint = f"/async/{scraper_name}" if scraper_name else "/async"
        response = requests.post(self._make_api_url(endpoint), json=data)
        self._raise_for_status(response)
        return response.json()

    def create_sync_task(self, data: Dict[str, Any], scraper_name: Optional[str] = None) -> dict:
        endpoint = f"/sync/{scraper_name}" if scraper_name else "/sync"
        response = requests.post(self._make_api_url(endpoint), json=data)
        self._raise_for_status(response)
        return response.json()

    def _make_api_url(self, path: str) -> str:
        return f"{self._api_url}{path}"

    def _raise_for_status(self, response: requests.Response) -> None:
        try:
            response.raise_for_status()
        except requests.HTTPError as e:
            error_message = f"HTTP error occurred: {e}"
            if response.text:
                error_message += f"\nAPI response: {response.text}"
            raise RuntimeError(error_message) from e
