import queue
import threading
from http.client import HTTPConnection, HTTPSConnection
from urllib.parse import urlparse

class HTTPConnectionPool:
    def __init__(self, host, port=None, timeout=None, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = "https" if _proxy and _proxy.startswith("https") else "http"
        self.ConnectionCls = HTTPSConnection if self.scheme == "https" else HTTPConnection
        self.QueueCls = queue.LifoQueue
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = self.QueueCls(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.headers = headers or {}
        self.conn_kw = conn_kw

    def _new_conn(self):
        conn = self.ConnectionCls(
            self.host,
            port=self.port,
            timeout=self.timeout,
            **self.conn_kw
        )
        if self.proxy:
            proxy_url = urlparse(self.proxy)
            conn.set_tunnel(self.host, self.port, headers=self.proxy_headers)
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        if self.pool.qsize() < self.pool.maxsize:
            self.pool.put(conn)
        else:
            conn.close()

    def _make_request(self, conn, method, url, body=None, headers=None, retries=None, timeout=None, **response_kw):
        if not headers:
            headers = {}
        for key, value in self.headers.items():
            headers.setdefault(key, value)
        conn.request(method, url, body=body, headers=headers)
        response = conn.getresponse()
        return response

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=None, pool_timeout=None, **response_kw):
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, body, headers, retries, timeout, **response_kw)
        except Exception as e:
            self._put_conn(None)
            raise e
        else:
            return response
        finally:
            self._put_conn(conn)

    @staticmethod
    def _close_pool_connections(pool):
        while not pool.empty():
            conn = pool.get_nowait()
            conn.close()

    def close(self):
        self._close_pool_connections(self.pool)
