import requests

class AccuWeatherClient:
    def __init__(self, api_key: str = "") -> None:
        self.api_key = api_key
        self.base_url = "http://dataservice.accuweather.com"

    def get_location_key(self, city: str) -> str:
        search_url = f"{self.base_url}/locations/v1/cities/search"
        params = {
            "apikey": self.api_key,
            "q": city
        }
        response = requests.get(search_url, params=params)
        if response.status_code == 200 and response.json():
            return response.json()[0]["Key"]
        return ""

    def get_weather(self, city: str, extensions: str = "all") -> str:
        if not self.api_key:
            return "No API key provided."

        location_key = self.get_location_key(city)
        if not location_key:
            return "Could not find the specified city."

        if extensions == "all":
            weather_url = f"{self.base_url}/currentconditions/v1/{location_key}"
        elif extensions == "base":
            weather_url = f"{self.base_url}/forecasts/v1/daily/5day/{location_key}"
        else:
            return "Invalid extension parameter. Use 'all' for current conditions or 'base' for 5-day forecast."

        params = {
            "apikey": self.api_key,
            "details": "true"
        }
        response = requests.get(weather_url, params=params)

        if response.status_code != 200:
            return f"Failed to retrieve weather data: {response.status_code}"

        return response.json()
