            response = requests.post(f"{node}/transactions/new", json=payload)
            if response.status_code == 201:
                return True, response.json()
            return False, None
        except requests.exceptions.RequestException:
            return False, None

    def register_nodes(self, node: str, nodes: list) -> Tuple[bool, Optional[Dict[str, Any]]]:
        payload = {'nodes': nodes}
        try:
            response = requests.post(f"{node}/nodes/register", json=payload)
            if response.status_code == 201:
                return True, response.json()
            return False, None
        except requests.exceptions.RequestException:
            return False, None

    def is_valid_chain(self, chain: list) -> bool:
        last_block = chain[0]
        current_index = 1

        while current_index < len(chain):
            block = chain[current_index]
            if block['previous_hash'] != self.hash(last_block):
                return False
            if not self.valid_proof(last_block['proof'], block['proof']):
                return False
            last_block = block
            current_index += 1
        return True

    @staticmethod
    def hash(block: dict) -> str:
        import hashlib
        block_string = str(block).encode()
        return hashlib.sha256(block_string).hexdigest()

    @staticmethod
    def valid_proof(last_proof: int, proof: int) -> bool:
        guess = f'{last_proof}{proof}'.encode()
        guess_hash = hashlib.sha256(guess).hexdigest()
        return guess_hash[:4] == "0000"
