import requests
import base64
from urllib.parse import urlencode

class AmazonSSO:
    def __init__(self, access_token: str = None, refresh_token: str = None):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = "YOUR_CLIENT_ID"
        self.client_secret = "YOUR_CLIENT_SECRET"
        self.user_pool_id = "YOUR_USER_POOL_ID"
        self.region = "us-east-1"
        self.user_info = {}

    def get_new_token(self) -> str:
        token_url = f"https://{self.region}.aws.amazon.com/login/oauth2/token"
        auth_header = base64.b64encode(f"{self.client_id}:{self.client_secret}".encode()).decode()
        payload = {
            "grant_type": "refresh_token",
            "refresh_token": self.refresh_token,
            "client_id": self.client_id
        }
        headers = {
            "Content-Type": "application/x-www-form-urlencoded",
            "Authorization": f"Basic {auth_header}"
        }

        response = requests.post(token_url, data=payload, headers=headers)
        if response.status_code == 200:
            tokens = response.json()
            self.access_token = tokens.get("access_token")
            return self.access_token
        else:
            raise Exception("Failed to refresh token")

    def get_user_info(self) -> dict:
        if not self.access_token:
            self.get_new_token()

        user_info_url = f"https://{self.region}.aws.amazon.com/login/oauth2/userInfo"
        headers = {
            "Authorization": f"Bearer {self.access_token}"
        }

        response = requests.get(user_info_url, headers=headers)
        if response.status_code == 200:
            self.user_info = response.json()
            return self.user_info
        else:
            raise Exception("Failed to retrieve user info")

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str = None):
        instance = AmazonSSO()
        token_url = f"https://{instance.region}.aws.amazon.com/login/oauth2/token"

        payload = {
            "grant_type": "authorization_code",
            "code": code,
            "client_id": instance.client_id
        }

        if redirect_uri:
            payload["redirect_uri"] = redirect_uri

        auth_header = base64.b64encode(f"{instance.client_id}:{instance.client_secret}".encode()).decode()
        headers = {
            "Content-Type": "application/x-www-form-urlencoded",
            "Authorization": f"Basic {auth_header}"
        }

        response = requests.post(token_url, data=urlencode(payload), headers=headers)
        if response.status_code == 200:
            tokens = response.json()
            instance.access_token = tokens.get("access_token")
            instance.refresh_token = tokens.get("refresh_token")
            return instance, None
        else:
            return None, response.text
